const pressedKeys = new Set();
document.addEventListener('keydown', (event) => {
	pressedKeys.add(event.key);  // Add the key to the set when pressed
});

	// Listen for keyup events (when a key is released)
document.addEventListener('keyup', (event) => {
	pressedKeys.delete(event.key);  // Remove the key from the set when released
});

let fov = 70;

function update( event ) {
	var player = this.getObjectByName( 'PlayerCam' );

	function isKeyPressed(key) {
		return pressedKeys.has(key);
	}

	const directionVector = new THREE.Vector3();
    player.getWorldDirection(directionVector);

	let direction = new THREE.Vector3();
	direction.set(0, 1.248, 0);
	direction = player.position;

	let speedMultiplier = 0.1;

	function checkKeys() {
		if (isKeyPressed("w")) {
			direction.add(directionVector.multiplyScalar(speedMultiplier));
		} else if (isKeyPressed("s")) {
			direction.sub(directionVector.multiplyScalar(speedMultiplier));
		}


		if (isKeyPressed("a")) {
			player.rotation.y = player.rotation.y + 0.05
		} else if (isKeyPressed("d")) {
			player.rotation.y = player.rotation.y - 0.05
		}
	}

	//const cursorPositionElement = document.getElementById('cursorPosition');

	// Call the function to check key states (you can call this in your loop or wherever needed)
	checkKeys();

	this.fov = fov;
	//player.rotation.x = -20.63;
}
